import axios from 'axios';

class HeroService {
    constructor() {
        this.api = axios.create({
            baseURL: 'http://localhost:5000/api/heroes', // Cambia esto a la URL de tu API
        });
    }

    getHeroes() {
        return this.api.get('/').then(response => response.data);
    }

    addHero(hero) {
        return this.api.post('/', hero).then(response => response.data);
    }

    updateHero(hero) {
        return this.api.put(`/${hero._id}`, hero).then(response => response.data);
    }

    deleteHero(id) {
        return this.api.delete(`/${id}`).then(response => response.data);
    }
}

export default HeroService;
