import React, { useState } from 'react';
import HeroItem from './HeroItem';
import DeleteHeroConfirmation from './DeleteHeroConfirmation';

function HeroList({ heroes, onDeleteHero, onEditHero }) {
    const [heroToDelete, setHeroToDelete] = useState(null);

    const handleDeleteConfirmation = (hero) => {
        setHeroToDelete(hero);
    };

    const handleDeleteCancel = () => {
        setHeroToDelete(null);
    };

    const handleDelete = async () => {
        if (heroToDelete) {
            await onDeleteHero(heroToDelete._id);
            setHeroToDelete(null);
        }
    };

    return (
        <div>
            {heroToDelete && (
                <DeleteHeroConfirmation
                    heroName={heroToDelete.name}
                    onDeleteHero={handleDelete}
                    onCancel={handleDeleteCancel}
                />
            )}
            <table className="hero-table">
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Casa publicadora</th>
                        <th>GÃ©nero</th>
                        <th>Altura (cm)</th>
                        <th>Peso (kg)</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {heroes.map(hero => (
                        <HeroItem
                            key={hero._id}
                            hero={hero}
                            onDelete={() => handleDeleteConfirmation(hero)}
                            onEdit={() => onEditHero(hero)}
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default HeroList;
