import React, { useState, useEffect } from 'react';

const EditHeroScreen = ({ hero, onUpdateHero, onCancel }) => {
    const [editedHero, setEditedHero] = useState(hero);
    const [errors, setErrors] = useState({});

    useEffect(() => {
        setEditedHero(hero);
    }, [hero]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedHero(prevHero => ({
            ...prevHero,
            [name]: value
        }));
    };

    const validateForm = () => {
        let formErrors = {};
        if (!editedHero.name.trim()) formErrors.name = 'El nombre es requerido';
        if (!editedHero.publisher.trim()) formErrors.publisher = 'La casa publicadora es requerida';
        if (!editedHero.race.trim()) formErrors.race = 'La raza es requerida';
        if (!editedHero.gender) formErrors.gender = 'El género es requerido';
        if (!editedHero.height) formErrors.height = 'La altura es requerida';
        else if (isNaN(editedHero.height) || Number(editedHero.height) <= 0) formErrors.height = 'La altura debe ser un número positivo';
        if (!editedHero.weight) formErrors.weight = 'El peso es requerido';
        else if (isNaN(editedHero.weight) || Number(editedHero.weight) <= 0) formErrors.weight = 'El peso debe ser un número positivo';
        return formErrors;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formErrors = validateForm();
        if (Object.keys(formErrors).length === 0) {
            onUpdateHero(editedHero);
        } else {
            setErrors(formErrors);
        }
    };

    return (
        <div className="hero-form">
            <h2 className="hero-gradient" style={{ textAlign: 'center', marginBottom: '30px', fontSize: '2.5em' }}>Editar Héroe</h2>
            <form onSubmit={handleSubmit}>
                {/* Form fields here */}
                <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '30px' }}>
                    <button type="button" onClick={onCancel} className="hero-button" style={{ backgroundColor: '#ff4500' }}>
                        Cancelar
                    </button>
                    <button type="submit" className="hero-button">
                        Actualizar Héroe
                    </button>
                </div>
            </form>
        </div>
    );
};

export default EditHeroScreen;
