import React from 'react';

const DeleteHeroConfirmation = ({ heroName, onDeleteHero, onCancel }) => {
    return (
        <div className="hero-confirmation">
            <h2 className="hero-gradient" style={{ textAlign: 'center', marginBottom: '30px', fontSize: '2em' }}>Confirmar Eliminación</h2>
            <p style={{ textAlign: 'center', marginBottom: '30px' }}>
                ¿Estás seguro de que deseas eliminar al héroe <strong>{heroName}</strong>? Esta acción no se puede deshacer.
            </p>
            <div style={{ textAlign: 'center' }}>
                <button className="hero-button" onClick={onDeleteHero} style={{ marginRight: '15px' }}>Eliminar</button>
                <button className="hero-button hero-button-cancel" onClick={onCancel}>Cancelar</button>
            </div>
        </div>
    );
};

export default DeleteHeroConfirmation;
