import React, { useState } from 'react';
import HeroDashboard from './components/Dashboard';
import CreateHeroScreen from './components/CreateHeroScreen';
import EditHeroScreen from './components/EditHeroScreen';
import DeleteHeroConfirmation from './components/DeleteHeroConfirmation';

const App = () => {
  const [heroes, setHeroes] = useState([
    // Ejemplo de datos de héroes
    { id: 1, name: 'Superman', publisher: 'DC Comics', race: 'Kryptonian', gender: 'Male', alignment: 'good', height: 191, weight: 107 },
    { id: 2, name: 'Batman', publisher: 'DC Comics', race: 'Human', gender: 'Male', alignment: 'good', height: 188, weight: 95 },
  ]);

  const [currentScreen, setCurrentScreen] = useState('dashboard');
  const [selectedHero, setSelectedHero] = useState(null);

  const handleCreateHero = (newHero) => {
    setHeroes([...heroes, { id: heroes.length + 1, ...newHero }]);
    setCurrentScreen('dashboard');
  };

  const handleEditHero = (updatedHero) => {
    setHeroes(heroes.map(hero => hero.id === updatedHero.id ? updatedHero : hero));
    setCurrentScreen('dashboard');
  };

  const handleDeleteHero = (heroId) => {
    setHeroes(heroes.filter(hero => hero.id !== heroId));
    setCurrentScreen('dashboard');
  };

  const handleSelectHero = (heroId) => {
    const hero = heroes.find(h => h.id === heroId);
    setSelectedHero(hero);
    setCurrentScreen('editHero');
  };

  const handleDeleteConfirmation = (heroId) => {
    const hero = heroes.find(h => h.id === heroId);
    setSelectedHero(hero);
    setCurrentScreen('deleteHero');
  };

  const handleCancel = () => {
    setCurrentScreen('dashboard');
    setSelectedHero(null);
  };

  return (
    <div className="app-container">
      {currentScreen === 'dashboard' && (
        <HeroDashboard
          heroes={heroes}
          onCreateHero={() => setCurrentScreen('createHero')}
          onEditHero={handleSelectHero}
          onDeleteHero={handleDeleteConfirmation}
        />
      )}
      {currentScreen === 'createHero' && (
        <CreateHeroScreen
          onCreateHero={handleCreateHero}
          onCancel={handleCancel}
        />
      )}
      {currentScreen === 'editHero' && selectedHero && (
        <EditHeroScreen
          hero={selectedHero}
          onUpdateHero={handleEditHero}
          onCancel={handleCancel}
        />
      )}
      {currentScreen === 'deleteHero' && selectedHero && (
        <DeleteHeroConfirmation
          heroName={selectedHero.name}
          onDeleteHero={() => {
            handleDeleteHero(selectedHero.id);
            setSelectedHero(null);
          }}
          onCancel={handleCancel}
        />
      )}
    </div>
  );
};

export default App;
