import React, { useState } from 'react';
import DeleteHeroConfirmation from './DeleteHeroConfirmation';

const HeroList = ({ heroes, onDeleteHero, onEditHero }) => {
    const [heroToDelete, setHeroToDelete] = useState(null);

    const handleDeleteConfirmation = (hero) => {
        setHeroToDelete(hero);
    };

    const handleDeleteCancel = () => {
        setHeroToDelete(null);
    };

    const handleDelete = async () => {
        if (heroToDelete) {
            try {
                await onDeleteHero(heroToDelete._id); // Asume que heroToDelete._id es el ID del héroe
                setHeroToDelete(null);
            } catch (error) {
                console.error('Error al eliminar el héroe:', error);
            }
        }
    };

    return (
        <div>
            {heroToDelete && (
                <DeleteHeroConfirmation
                    heroName={heroToDelete.name}
                    onDeleteHero={handleDelete}
                    onCancel={handleDeleteCancel}
                />
            )}
            <div className="hero-list">
                {heroes.length === 0 ? (
                    <p>No hay héroes disponibles.</p>
                ) : (
                    heroes.map(hero => (
                        <div key={hero._id} className="hero-card">
                            <h3>{hero.name}</h3>
                            <p><strong>Publicadora:</strong> {hero.publisher}</p>
                            <p><strong>Género:</strong> {hero.gender}</p>
                            <p><strong>Altura:</strong> {hero.height}</p>
                            <p><strong>Peso:</strong> {hero.weight}</p>
                            <button onClick={() => onEditHero(hero)} className="hero-button">Editar</button>
                            <button onClick={() => handleDeleteConfirmation(hero)} className="hero-button hero-button-delete">Eliminar</button>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default HeroList;
